mapper.select:
select,UN_PAY,CASE WHEN SUM(repay1.TOTLE_MONEY) is null THEN 0 ELSE SUM(repay1.TOTLE_MONEY) END AS YET_PAY from (select,CASE WHEN SUM(repay0.TOTLE_MONEY) IS null THEN 0 ELSE SUM(repay0.TOTLE_MONEY) END AS UN_PAY from T_DEBITANDCREDIT debit left join T_REPAYALOAN repay0 on debit.DT_ID = repay0.DEBIT_ID AND repay0.STATUS = 0
and D_LNR_ID = #{userId}
GROUP BY DT_ID,D_ITT_ID,D_LNR_ID,D_PRO_ID,D_PRO_NAME,D_PRO_LMT,D_TIMES,D_ITT_RA,D_MONEY,D_BEF,D_AFT,D_DATE,LAST_REPAY_DATE,STUS,LOAN_DATE,LOAN_MONEY,TOTLE_INTREST,SERVICE_CHARGE,LINE_OF_CREDIT,MERCHANT_NUM) a left join T_REPAYALOAN repay1 on DT_ID = repay1.DEBIT_ID AND repay1.STATUS = 1 GROUP BY DT_ID,D_ITT_ID,D_LNR_ID,D_PRO_ID,D_PRO_NAME,D_PRO_LMT,D_TIMES,D_ITT_RA,D_MONEY,D_BEF,D_AFT,D_DATE,UN_PAY,LAST_REPAY_DATE,STUS,LOAN_DATE,,LOAN_MONEY,TOTLE_INTREST,SERVICE_CHARGE,LINE_OF_CREDIT,MERCHANT_NUM

select

from t_debitandcredit
where D_PRO_ID = #{proId} AND D_LNR_ID = #{userId} AND STUS = #{stus}
